#!/usr/bin/env bashset -euo pipefail# Ensure mount dirs exist and have correct permsmkdir -p /cron /datachmod 755 /cron /data# Install cron file if presentif [ -f /app/cron/2fa-cron ]; then  chmod 0644 /app/cron/2fa-cron  crontab /app/cron/2fa-cronfi# Start cron in background (tries service/init scripts)if command -v service >/dev/null 2>&1; then  service cron start || /etc/init.d/cron start || cron &else  cron &fisleep 1# Start the application server in foreground.# Try gunicorn -> uvicorn -> fallback to direct python.if command -v gunicorn >/dev/null 2>&1; then  exec gunicorn --bind 0.0.0.0:8080 --workers 1 app:appelse  if python -c "import uvicorn" >/dev/null 2>&1; then    exec python -m uvicorn app:app --host 0.0.0.0 --port 8080  else    exec python -u app/app.py  fifi